
$("#apiMsg").hide();$("#apiUsername").hide();var whereFields="";$("#whereFields").on("change keyup paste",function(){whereFields=$(this).val()});var selectFields="";$("#selectFields").on("change keyup paste",function(){selectFields=$(this).val()});var username=$("#apiUsername").text();var apiBtn=$("#apiBtn");apiBtn.click(function(){var a=$.ajax({url:"http://localhost:3000/"+username+"/"+encodeURIComponent(whereFields)+"/"+encodeURIComponent(selectFields),method:"GET",async:true,dataType:"text"});a.done(function(b){console.log("PIC-SURE API data loaded successfully.");$("#apiMsg").show()});a.fail(function(){console.log("Ajax error - failed to load data from PIC-SURE API")})});