
$("#apiMsg").hide();$("#apiUsername").hide();var apiKey="";$("#apiKey").on("change keyup paste",function(){apiKey=$(this).val()});var queryJson="";$("#queryJson").on("change keyup paste",function(){queryJson=$(this).val()});var username=$("#apiUsername").text();var apiBtn=$("#apiBtn");apiBtn.click(function(){var a=$.ajax({url:"http://localhost:3000/"+username+"/"+apiKey,method:"POST",async:true,contentType:"application/json",data:queryJson,dataType:"text"});a.done(function(b){console.log("PIC-SURE API data loaded successfully.");$("#apiMsg").show()});a.fail(function(){console.log("Ajax error - failed to load data from PIC-SURE API")})});