$.validator.addMethod("nowhitespace", function(b, a){return this.optional(a) || /^\S+$/i.test(b)}, "No white space please"); $(document).ready(function(){$("#login").validate({rules:{username:{minlength:3, nowhitespace:true, required:true}, password:{minlength:5, maxlength:25, nowhitespace:true, required:true}}, messages:{username:"Please enter a valid username.", password:"Please enter your password."}, highlight:function(a){$(a).closest(".form-group").addClass("has-error")}, unhighlight:function(a){$(a).closest(".form-group").removeClass("has-error")}, errorElement:"span", errorClass:"help-block", errorPlacement:function(a, b){if (b.parent(".input-group").length){a.insertAfter(b.parent())} else{a.insertAfter(b)}}}); $("#registration").validate({rules:{username:{minlength:3, nowhitespace:true, required:true}, email:{email:true, required:true}, password:{minlength:5, maxlength:25, nowhitespace:true, required:true}, confirmPassword:{equalTo:"#password"}}, messages:{username:"Please enter a valid username (no white space).", email:"Please enter a valid email.", password:"Please enter valid a password (5-25 chars).", confirmPassword:"Please reenter password."}, highlight:function(a){$(a).closest(".form-group").addClass("has-error")}, unhighlight:function(a){$(a).closest(".form-group").removeClass("has-error")}, errorElement:"span", errorClass:"help-block", errorPlacement:function(a, b){if (b.parent(".input-group").length){a.insertAfter(b.parent())} else{a.insertAfter(b)}}})});