$(document).ready(function(){$("#password_reset").validate({rules:{password:{minlength:5,maxlength:25,nowhitespace:true,required:true},confirmPassword:{equalTo:"#password"}},messages:{password:"Please enter valid a password (5-25 chars).",confirmPassword:"Please reenter password.",},highlight:function(a){$(a).closest(".form-group").addClass("has-error")},unhighlight:function(a){$(a).closest(".form-group").removeClass("has-error")},errorElement:"span",errorClass:"help-block",errorPlacement:function(a,b){if(b.parent(".input-group").length){a.insertAfter(b.parent())}else{a.insertAfter(b)}}})});